import { initAuth0 } from '@auth0/nextjs-auth0';
// import config from './config';

export default initAuth0({
  domain: 'auth.dseapps.dev',
  clientId: 'vwsI9UiL0Q7LXSOe2imitZvPg1ffRm4C',
  clientSecret: '1JZa4zcHudpLBCQ3eqSnbHWc0s8WKuNvGRnuHjdDHJA8XddxAx-G254y4xOfJ-9C',
  scope: 'openid profile',
  redirectUri: 'http://192.168.7.46:3000/api/callback',
  postLogoutRedirectUri: 'http://192.168.7.46:3000/',
  session: {
    // The secret used to encrypt the cookie.
    cookieSecret: 'DC6cP0TilntxWDL4I+pSaAenkqUctR5gyVyFeOW8/Kw=',
    // The cookie lifetime (expiration) in seconds. Set to 8 hours by default.
    cookieLifetime: 60 * 60 * 8,
    // (Optional) The cookie domain this should run on. Leave it blank to restrict it to your domain.
    cookieDomain: '192.168.7.46',
    // (Optional) SameSite configuration for the session cookie. Defaults to 'lax', but can be changed to 'strict' or 'none'. Set it to false if you want to disable the SameSite setting.
    cookieSameSite: 'lax',
    // (Optional) Store the id_token in the session. Defaults to false.
    storeIdToken: true,
    // (Optional) Store the access_token in the session. Defaults to false.
    storeAccessToken: false,
    // (Optional) Store the refresh_token in the session. Defaults to false.
    storeRefreshToken: false
  },
  oidcClient: {
    // (Optional) Configure the timeout in milliseconds for HTTP requests to Auth0.
    httpTimeout: 2500,
    // (Optional) Configure the clock tolerance in milliseconds, if the time on your server is running behind.
    clockTolerance: 10000
  }
});
